import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetch('http://127.0.0.1:8000/posts/')  // your Django API endpoint
      .then(response => response.json())
      .then(data => {
        setPosts(data); // Update state with the posts data
      })
      .catch(error => console.error('Error fetching posts:', error));
  }, []);

  return (
    <div>
      <h1>Blog Posts</h1>
      {posts.length === 0 ? (
        <p>No posts available!</p>
      ) : (
        posts.map(post => (
          <div key={post.id}>
            <h2><Link to={`/posts/${post.id}/`}>{post.title}</Link></h2> {/* Link to individual post */}
            <p>{post.body}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default HomePage;
